import React,{useState, useEffect} from 'react';
import '../css/Main.css';
import RandomButton from '../components/RandomButton';
import Stratigic from '../components/Strategic';
import Proficient from '../components/Proficient';
import Advanced from '../components/Advanced';
import { set } from 'mongoose';

export default (props) => {
    const[num1,setNum1] = useState(0);
    const[num2,setNum2] = useState(0);
    const[num3,setNum3] = useState(0);
    const[l1Think, setL1Think] = useState("");
    const[l2Think, setL2Think] = useState("");
    const[l3Think, setL3Think] = useState("");

    // level 1 options //
    const L1ThinkSkills = ["list", "define", "state", "summarize", "describe", "discuss", "explain", "identify", "paraphrase", "compare & contrast", "sequence", "recognize"];
    // level 2 options //
    const L2ThinkSkills = ["compare & contrast", "gather evidence to support", "categorize", "support", "interpret", "examine", "question", "prove", "defend"];
    // level 3 options //
    const L3ThinkSkills = ["compare & contrast", "interpret", "defferentiate", "prove", "judge", "determine the relevance", "note abiguity", "state & test assumptions", "redesign", "add to", "gather evidence to support", "question", "evaluate", "defend"];

    useEffect(() =>{
        setL1Think(L1ThinkSkills[num1]);
        setL2Think(L2ThinkSkills)
    },[]);


    return(
        <div>
            <div className='col-sm-12 text-center mt-2'>
                <h1 className='title'> Depth & Complexity </h1>
                <h3>Alignment Project</h3>
            </div>
            <RandomButton setNum1={setNum1} setNum2={setNum2} setNum3={setNum3} />
            <Stratigic className="strategic" L1Think = {l1Think} setL1Think={setL1Think} />
            <Proficient className="proficient" L2Think = {l2Think} setL1Think={setL2Think} />
            <Advanced  className="advanced" L3Think = {l3Think} setL1Think={setL3Think} />
        </div>
    )

}
