import React,{useState} from 'react';
import '../css/RandomButton.css';

export default (props) => {
    const[l1Think, setL1Think] = useState("");
    const[l2Think, setL2Think] = useState("");
    const[l3Think, setL3Think] = useState("")
    const L1ThinkSkills = ["list", "define",  "state", "summarize", "describe", "discuss", "explain", "identify", "paraphrase", "compare & contrast", "sequence", "recognize"];

    const onClick = (e) => {
        e.preventDefault();
        let num = Math.floor(Math.random() * Math.floor(10));
        setL1Think(L1ThinkSkills[num]);
    };
    

    return(
        <div className='col-sm-12 text-center mt-3'>
            <button className='btn col-sm-6 btn-primary btn-lg RandButt' onClick={onClick}> Randomize </button>
        </div>
    )

}
