import React,{useState, useEffect} from 'react';
import '../css/Main.css';
import Stratigic from '../components/Strategic';
import Proficient from '../components/Proficient';
import Advanced from '../components/Advanced';


export default (props) => {
    const[num1,setNum1] = useState(0);
    const[num2,setNum2] = useState(0);
    const[num3,setNum3] = useState(0);
    const[l1Think, setL1Think] = useState("");
    const[l2Think, setL2Think] = useState("");
    const[l3Think, setL3Think] = useState("");

    // level 1 options //
    const L1ThinkSkills = ["list", "define", "state", "summarize", "describe", "discuss", "explain", "identify", "paraphrase", "compare & contrast", "sequence", "recognize"];
    // level 2 options //
    const L2ThinkSkills = ["compare & contrast", "gather evidence to support", "categorize", "support", "interpret", "examine", "question", "prove", "defend"];
    // level 3 options //
    const L3ThinkSkills = ["compare & contrast", "interpret", "defferentiate", "prove", "judge", "determine the relevance", "note abiguity", "state & test assumptions", "redesign", "add to", "gather evidence to support", "question", "evaluate", "defend"];

    const onClick = (e) => {
        e.preventDefault();
        let N1 = Math.floor(Math.random() * Math.floor(12));
        let N2 = Math.floor(Math.random() * Math.floor(9));
        let N3 = Math.floor(Math.random() * Math.floor(14));
        setNum1(N1);
        setNum2(N2);
        setNum3(N3);
        setL1Think(L1ThinkSkills[num1]);
        setL2Think(L2ThinkSkills[num2]);
        setL3Think(L3ThinkSkills[num3]);
    };

    return(
        <div>
            
            <div className='col-sm-12 text-center mt-2'>
                <h1 className='title'> Depth & Complexity </h1>
                <h3>Alignment Project</h3>
            </div>

            <div className='col-sm-12 text-center mt-3'>
                <button className='btn col-sm-6 btn-primary btn-lg RandButt' onClick={onClick}> Randomize </button>
            </div>

            <div className='mt-5 text-center strategic'>
                <div className='col-sm-12'>
                    <h4 className='title'> Strategic </h4>
                </div>
                <div className='statement'>
                    <p>I can {l1Think} the (dimension of differentiation) of the (subject).</p>
                    <p>I will get/organize information by (research skill) and share my findings by/in (product).</p>
                </div>
            </div>
            
            <div className='mt-5 text-center proficient'>
                <div className='col-sm-12'>
                    <h4 className='title'> Proficient </h4>
                </div>
                <div className='statement'>
                    <p>I can (Thinking Skill) the (dimension of differentiation) of the (subject).</p>
                    <p>I will get/organize information by (research skill) and share my findings by/in (product).</p>
                </div>
            </div>
            
            <Advanced L3Think={l3Think} setL1Think={setL3Think} className="advanced"/>
        </div>
    )
}
