import React,{useState} from 'react';
import '../css/Main.css';
import Accross from '../pics/AcrossDisciplines.jpg';
import BigIdea from '../pics/BigIdea.jpg';
import Details from '../pics/Details.jpg';
import Ethics from '../pics/Ethics.jpg';
import Language from '../pics/LanguageOfDiscipline.jpg';
import Multiple from '../pics/MultiplePerspectives.jpg';
import OverTime from '../pics/OverTime.jpg';
import Patterns from '../pics/Patterns.jpg';
import Rules from '../pics/Rules.jpg';
import Trends from '../pics/Trends.jpg';
import Unanswered from '../pics/UnansweredQs.jpg';
import box from '../pics/whitebox.jpg';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';
import FadeIn from 'react-fade-in';


export default (props) => {
    const[l1Think, setL1Think] = useState("_______");
    const[l2Think, setL2Think] = useState("_______");
    const[l3Think, setL3Think] = useState("_______");
    const[resource1, setResource1] = useState("_______");
    const[resource2, setResource2] = useState("_______");
    const[resource3, setResource3] = useState("_______");
    const[product1, setProduct1] = useState("_______");
    const[product2, setProduct2] = useState("_______");
    const[product3, setProduct3] = useState("_______");
    const[Icon1, setIcon1] = useState(box);
    const[Icon2, setIcon2] = useState(box);
    const[Icon3, setIcon3] = useState(box);
    const[Icon4, setIcon4] = useState("");
    const[subject, setSubject] = useState("_________");

    // level 1 Thinking Skills //
    const L1ThinkSkills = ["list", "define", "state", "summarize", "describe", "discuss", "explain", "identify", "paraphrase", "compare & contrast", "sequence", "recognize"];
    // level 2 Thinking Skills //
    const L2ThinkSkills = ["compare & contrast", "gather evidence to support", "categorize", "support", "interpret", "examine", "question", "prove", "defend"];
    // level 3 Thinking Skills //
    const L3ThinkSkills = ["compare & contrast", "interpret", "defferentiate", "prove", "judge", "determine the relevance", "note abiguity", "state & test assumptions", 
                            "redesign", "add to", "gather evidence to support", "question", "evaluate", "defend"];
    // All resources //
    const Resources = ["a textbook", "an encyclopedia", "a library book", "a magazine", "a newspaper", "an interview", "an expert", "a website", "an online encyclopedia", 
                        "a journal", "an article", "Wikipedia", "an outline", "a video", "a novel"];
    // All products //
    const Products = ["a chart", "a drawling", "a timeline", "a diagram", "a graphic organizer", "a comic", "a map", "a book cover", "a poster", "a museum exhibit", 
                        "a mobile", "a sculpture", "an art gallery", "a debate", "a panel discussion", "a lesson taught to the class", "a report", "a play", "a press conference", 
                        "a TV show", "a monologue", "a movie review", "a song", "a powerpoint", "Google slides", "a photo essay", "a news report", "a webpage", "a persuasive essay", 
                        "a letter", "a children's story", "a poem", "a diary entry", "a reader's theater", "a game", "a puzzle", "a paragraph", "a travelogue"];
    // level 1 Icons //
    const Icons1 = [Language, Details, Patterns, Rules, Ethics, BigIdea, Multiple, OverTime];
    // level 2 Icons //
    const Icons2 = [Trends, Unanswered, Rules, Patterns, OverTime, Multiple, Language, Ethics, Details, BigIdea, Accross];
    // Icon combos for level 3 //
    const TrendCombo = [[Trend,Multiple], [Trend,OverTime], [Trend,Rules], [Trend,Ethics], [Trend,Accross], [Language,Rules], [Language,OverTime], [Language,Accross], [Language,Multiple], [Language,Trends], [Language,Patterns], [Language,Ethics], [Details,Rules], [Details,Patterns], [Details,Language], [Details,Trends], [Details,Unanswered], [Details,Ethics], [Details,Multiple], [Details,OverTime], [Details,Accross], [Patterns,Language], [Patterns,Details], [Patterns,Rules], [Patterns,Trends], [Patterns,BigIdea], [Patterns,Unanswered], [Patterns,Multiple], [Patterns,OverTime], [Patterns,Accross],

[Rules,Language], [Rules,Details], [Rules,Patterns], [Rules,Trends], [Rules,Unanswered], [Rules,Multiple], [Rules,OverTime], [Rules,Accross], [Rules,Ethics]

[Unanswered,BigIdea], [Unanswered,Details], [Unanswered,Patterns], [Unanswered,Rules], [Unanswered,Trends], [Unanswered,Ethics], [Unanswered,Language], [Unanswered,OverTime], [Unanswered,Accross], [Unanswered,Multiple]

[Ethics,OverTime], [Ethics,Language], [Ethics,Details], [Ethics,Patterns], [Ethics,Trends], [Ethics,Rules], [Ethics,BigIdea], [Ethics,Accross], [Ethics,Multiple], [Ethics,Unanswered]
[BigIdea,Patterns], [BigIdea,Rules], [BigIdea,Details], [BigIdea,Trends], [BigIdea,Language], [BigIdea,Ethics], [BigIdea,OverTime], [BigIdea,Multiple], [BigIdea,Accross]];


    const Randomize = (e) => {
        e.preventDefault();
        setIcon1(Icons1[Math.floor(Math.random() * Math.floor(8))]);
        setIcon2(Icons2[Math.floor(Math.random() * Math.floor(11))]);
        setIcon3(Icons3[Math.floor(Math.random() * Math.floor(11))]);
        setL1Think(L1ThinkSkills[Math.floor(Math.random() * Math.floor(12))]);
        setL2Think(L2ThinkSkills[Math.floor(Math.random() * Math.floor(9))]);
        setL3Think(L3ThinkSkills[Math.floor(Math.random() * Math.floor(14))]);
        setResource1(Resources[Math.floor(Math.random() * Math.floor(15))]);
        setResource2(Resources[Math.floor(Math.random() * Math.floor(15))]);
        setResource3(Resources[Math.floor(Math.random() * Math.floor(15))]);
        setProduct1(Products[Math.floor(Math.random() * Math.floor(38))]);
        setProduct2(Products[Math.floor(Math.random() * Math.floor(38))]);
        setProduct3(Products[Math.floor(Math.random() * Math.floor(38))]);

        if(Icon3 === Trends){
            setIcon4(TrendCombo[Math.floor(Math.random() * Math.floor(5))]);
        }
        if(Icon3 === Language){
            setIcon4(LanguageCombo[Math.floor(Math.random() * Math.floor(7))]);
        }
        if(Icon3 === Details){
            setIcon4(DetailsCombo[Math.floor(Math.random() * Math.floor(9))]);
        }
        if(Icon3 === Patterns){
            setIcon4(PatternsCombo[Math.floor(Math.random() * Math.floor(9))]);
        }
        if(Icon3 === Rules){
            setIcon4(RulesCombo[Math.floor(Math.random() * Math.floor(9))]);
        }
        if(Icon3 === Unanswered){
            setIcon4(UnansweredCombo[Math.floor(Math.random() * Math.floor(10))]);
        }
        if(Icon3 === Ethics){
            setIcon4(EthicsCombo[Math.floor(Math.random() * Math.floor(10))]);
        }
        if(Icon3 === BigIdea){
            setIcon4(BigIdeaCombo[Math.floor(Math.random() * Math.floor(9))]);
        }
        else if(Icon3 === Multiple || Icon3 === OverTime || Icon3 === Accross){
            setIcon4("");
        }
    };

    const subjectHandler = (e) => {
        setSubject(e.target.value);
    }

    const printDocument = () => {
        const input = document.getElementById('divToPrint');
        html2canvas(input)
            .then((canvas) => {
                const imgData = canvas.toDataURL('image/png');
                const pdf = new jsPDF();
                pdf.addImage(imgData, 'JPEG', 0, 0);
                pdf.save("download.pdf");
            });
    }

    return(
        <div>
        <div id='divToPrint'>
            <div className='col-sm-12 text-center mt-2'>
                <h1 className='MainTitle'> Depth & Complexity </h1>
                <h4 className='SecTitle'>Alignment Project</h4>
            </div>
            <div className ="mt-2">
