import React,{useState, useEffect} from 'react';
import '../css/Main.css';
import Accross from '../pics/AcrossDisciplines.jpg';
import BigIdea from '../pics/BigIdea.jpg';
import Details from '../pics/Details.jpg';
import Ethics from '../pics/Ethics.jpg';
import Language from '../pics/LanguageOfDiscipline.jpg';
import Multiple from '../pics/MultiplePerspectives.jpg';
import OverTime from '../pics/OverTime.jpg';
import Patterns from '../pics/Patterns.jpg';
import Rules from '../pics/Rules.jpg';
import Trends from '../pics/Trends.jpg';
import Unanswered from '../pics/UnansweredQs.jpg';

export default (props) => {
    const[l1Think, setL1Think] = useState("_______");
    const[l2Think, setL2Think] = useState("_______");
    const[l3Think, setL3Think] = useState("_______");
    const[resource1, setResource1] = useState("_______");
    const[resource2, setResource2] = useState("_______");
    const[resource3, setResource3] = useState("_______");
    const[product1, setProduct1] = useState("_______");
    const[product2, setProduct2] = useState("_______");
    const[product3, setProduct3] = useState("_______");
    const[Icon1, setIcon1] = useState();
    const[Icon2, setIcon2] = useState();
    const[Icon3, setIcon3] = useState();
    const[subject, setSubject] = useState("_______");


    // level 1 Thinking Skills //
    const L1ThinkSkills = ["list", "define", "state", "summarize", "describe", "discuss", "explain", "identify", "paraphrase", "compare & contrast", "sequence", "recognize"];
    // level 2 Thinking Skills //
    const L2ThinkSkills = ["compare & contrast", "gather evidence to support", "categorize", "support", "interpret", "examine", "question", "prove", "defend"];
    // level 3 Thinking Skills //
    const L3ThinkSkills = ["compare & contrast", "interpret", "defferentiate", "prove", "judge", "determine the relevance", "note abiguity", "state & test assumptions", "redesign", "add to", "gather evidence to support", "question", "evaluate", "defend"];
    // All resources //
    const Resources = ["a textbook", "an encyclopedia", "a library book", "a magazine", "a newspaper", "an interview", "an expert", "a website", "an online encyclopedia", "a journal", "an article", "Wikipedia", "an outline", "a video", "a novel"];
    // All products //
    const Products = ["a chart", "a drawling", "a timeline", "a diagram", "a graphic organizer", "a comic", "a map", "a book cover", "a poster", "a museum exhibit", "a mobile", "a sculpture", "an art gallery", "a debate", "a panel discussion", "a lesson taught to the class", "a report", "a play", "a press conference", "a TV show", "a monologue", "a movie review", "a song", "a powerpoint", "Google slides", "a photo essay", "a news report", "a webpage", "a persuasive essay", "a letter", "a children's story", "a poem", "a diary entry", "a reader's theater", "a game", "a puzzle", "a paragraph", "a travelogue"];
    // level 1 Icons //
    const Icons1 = [Language, Details, Patterns, Rules, Ethics, BigIdea, Multiple, OverTime];
    // level 2 Icons //
    const Icons2 = [Trends, Unanswered, Rules, Patterns, OverTime, Multiple, Language, Ethics, Details, BigIdea, Accross];
    // level 3 Icons //
    const Icons3 = [Trends, Unanswered, Rules, Patterns, OverTime, Multiple, Language, Ethics, Details, BigIdea, Accross];


    const Randomize = (e) => {
        e.preventDefault();
        setL1Think(L1ThinkSkills[Math.floor(Math.random() * Math.floor(12))]);
        setL2Think(L2ThinkSkills[Math.floor(Math.random() * Math.floor(9))]);
        setL3Think(L3ThinkSkills[Math.floor(Math.random() * Math.floor(14))]);
        setResource1(Resources[Math.floor(Math.random() * Math.floor(15))]);
        setResource2(Resources[Math.floor(Math.random() * Math.floor(15))]);
        setResource3(Resources[Math.floor(Math.random() * Math.floor(15))]);
        setProduct1(Products[Math.floor(Math.random() * Math.floor(38))]);
        setProduct2(Products[Math.floor(Math.random() * Math.floor(38))]);
        setProduct3(Products[Math.floor(Math.random() * Math.floor(38))]);
        setIcon1(Icons1[Math.floor(Math.random() * Math.floor(8))]);
        setIcon2(Icons2[Math.floor(Math.random() * Math.floor(11))]);
        setIcon3(Icons3[Math.floor(Math.random() * Math.floor(11))]);
    };

    const subjectHandler = (e) => {
        setSubject(e.target.value);
    }

    return(
        <div>
            <div className='col-sm-12 text-center mt-2'>
                <h1 className='title'> Depth & Complexity </h1>
            </div>

            <div className='col-sm-12 text-center mt-3'>
                <button className='col-sm-6 btn-primary btn-lg Randomizer' onClick={Randomize}> Randomize </button>
            </div>

            <div className="topic" method="POST" class="col-8 offset-2 mt-3">
                <div className="input-group mb-3">
                    <input type="text" name="subject" className="form-control topic" aria-describedby="basic-addon2" onChange={subjectHandler} placeholder="Enter Topic or Subject Name" />
                    <div className="input-group-append">
                        <button className="btn btn-info topic"> Submit </button>
                    </div>
                </div>
            </div>

            <div className='mt-4 text-center L1'>
                <div className='col-sm-12'>
                    <h3 className='title'> Strategic </h3>
                </div>
                <div className='statement'>
                    <p>I can {l1Think} the <img src={Icon1} alt={Icon1} className='Icon1' /> of the <br/>(subject).<br/>
                    I will get/organize information by {resource1} and share my findings by/in {product1}.</p>
                </div>
            </div>
            
            <div className='mt-4 text-center L2'>
                <div className='col-sm-12'>
                    <h3 className='title'> Proficient </h3>
                </div>
                <div className='statement'>
                    <p>I can {l2Think} the <img src={Icon2} alt={Icon2} className='Icon2' /> of the <br/>{subject}.<br/>
                    I will get/organize information by {resource2} and share my findings by/in {product2}.</p>
                </div>
            </div>
            
            <div className='mt-4 text-center L3'>
                <div className='col-sm-12'>
                    <h3 className='title'> Advanced </h3>
                </div>
                <div className='statement'>
                    <p>I can {l3Think} the <img src={Icon3} alt={Icon3} className='Icon3' /> of the <br/>(subject).<br/>
                    I will get/organize information by {resource3} and share my findings by/in {product3}.</p>
                </div>
            </div>
        </div>
    )
}
